// src/components/AppointmentBooking.js
import React, { useState } from 'react';
import Button from './common/Button';
import Input from './common/Input';

const Appointments = () => {
    const [appointments, setAppointments] = useState([]);
    const [showBookingForm, setShowBookingForm] = useState(false);
    const [formData, setFormData] = useState({
        doctorName: '',
        specialization: '',
        date: '',
        time: '',
        reason: ''
    });
    const [errors, setErrors] = useState({});

    const specializations = [
        'General Physician',
        'Cardiologist',
        'Dermatologist',
        'Neurologist',
        'Pediatrician',
        'Orthopedist',
        'Psychiatrist',
        'Dentist'
    ];

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.doctorName.trim()) {
            newErrors.doctorName = 'Doctor name is required';
        }
        if (!formData.specialization) {
            newErrors.specialization = 'Specialization is required';
        }
        if (!formData.date) {
            newErrors.date = 'Date is required';
        } else {
            const selectedDate = new Date(formData.date);
            const today = new Date();
            today.setHours(0, 0, 0, 0);
            if (selectedDate < today) {
                newErrors.date = 'Date cannot be in the past';
            }
        }
        if (!formData.time) {
            newErrors.time = 'Time is required';
        }
        if (!formData.reason.trim()) {
            newErrors.reason = 'Reason for visit is required';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        const newAppointment = {
            id: Date.now(),
            ...formData,
            status: 'Pending'
        };

        setAppointments(prev => [...prev, newAppointment]);
        setShowBookingForm(false);
        setFormData({
            doctorName: '',
            specialization: '',
            date: '',
            time: '',
            reason: ''
        });
    };

    const getStatusColor = (status) => {
        switch (status) {
            case 'Confirmed':
                return 'text-green-600';
            case 'Cancelled':
                return 'text-red-600';
            default:
                return 'text-yellow-600';
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center py-10">
            <div className="w-full max-w-4xl bg-white rounded-lg shadow-md p-6">
                <h2 className="text-3xl font-bold text-gray-800 mb-6">Appointments</h2>

                <div className="flex justify-end mb-6">
                    <Button
                        onClick={() => setShowBookingForm(!showBookingForm)}
                        className="bg-primary-600 hover:bg-primary-700 text-white"
                    >
                        {showBookingForm ? 'Hide Booking Form' : 'Book New Appointment'}
                    </Button>
                </div>

                {showBookingForm && (
                    <div className="bg-gray-50 p-6 rounded-md mb-6">
                        <h3 className="text-2xl font-semibold text-gray-700 mb-4">Book a New Appointment</h3>
                        <form onSubmit={handleSubmit} className="space-y-4">
                            <Input
                                label="Doctor's Name"
                                name="doctorName"
                                value={formData.doctorName}
                                onChange={handleChange}
                                required
                            />
                            <Input
                                label="Specialization"
                                name="specialization"
                                value={formData.specialization}
                                onChange={handleChange}
                                required
                            />
                            <Input
                                label="Date"
                                type="date"
                                name="date"
                                value={formData.date}
                                onChange={handleChange}
                                required
                            />
                            <Input
                                label="Time"
                                type="time"
                                name="time"
                                value={formData.time}
                                onChange={handleChange}
                                required
                            />
                            <Input
                                label="Reason for Appointment"
                                name="reason"
                                value={formData.reason}
                                onChange={handleChange}
                                required
                            />
                            <div className="flex justify-end">
                                <Button
                                    type="submit"
                                    className="bg-primary-600 hover:bg-primary-700 text-white"
                                >
                                    Confirm Booking
                                </Button>
                            </div>
                        </form>
                    </div>
                )}

                <h3 className="text-2xl font-semibold text-gray-700 mb-4">Your Appointments</h3>
                {appointments.length > 0 ? (
                    <div className="border-t border-gray-200 pt-4">
                        <ul className="divide-y divide-gray-200">
                            {appointments.map((appointment) => (
                                <li key={appointment.id} className="py-4 flex justify-between items-center">
                                    <div>
                                        <p className="text-lg font-medium text-gray-900">Dr. {appointment.doctorName} - {appointment.specialization}</p>
                                        <p className="text-sm text-gray-600">Date: {appointment.date} at {appointment.time}</p>
                                        <p className="text-sm text-gray-600">Reason: {appointment.reason}</p>
                                    </div>
                                    <div>
                                        <Button
                                            onClick={() => alert(`Rescheduling appointment with Dr. ${appointment.doctorName}`)}
                                            className="bg-yellow-500 hover:bg-yellow-600 text-white text-sm px-3 py-1 mr-2"
                                        >
                                            Reschedule
                                        </Button>
                                        <Button
                                            onClick={() => setAppointments(appointments.filter(app => app.id !== appointment.id))}
                                            className="bg-red-500 hover:bg-red-600 text-white text-sm px-3 py-1"
                                        >
                                            Cancel
                                        </Button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <div className="px-4 py-5 sm:px-6 text-center text-gray-500">
                        No appointments scheduled. Book your first appointment now!
                    </div>
                )}
            </div>
        </div>
    );
};

export default Appointments;
