import React, { useState, useRef, useLayoutEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import PersonalInfo from './PersonalInfo';
import MedicalInfo from './MedicalInfo';
import AccountSettings from './AccountSettings';
import { motion, AnimatePresence } from 'framer-motion';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('personal');
  const { user, updateUser } = useAuth();
  const tabRefs = useRef({});
  const underlineRef = useRef(null);
  const [underlineProps, setUnderlineProps] = useState({ left: 0, width: 0 });

  const tabs = [
    { id: 'personal', name: 'Personal Information' },
    { id: 'medical', name: 'Medical Information' },
    { id: 'settings', name: 'Account Settings' }
  ];

  useLayoutEffect(() => {
    const node = tabRefs.current[activeTab];
    if (node) {
      const { offsetLeft, offsetWidth } = node;
      setUnderlineProps({ left: offsetLeft, width: offsetWidth });
      console.log('Underline props:', { left: offsetLeft, width: offsetWidth });
    }
  }, [activeTab]);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'personal':
        return <PersonalInfo user={user} onUpdate={updateUser} />;
      case 'medical':
        return <MedicalInfo user={user} />;
      case 'settings':
        return <AccountSettings />;
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow">
          <div className="p-6 space-y-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Profile</h1>
              <p className="mt-2 text-sm text-gray-500">
                Manage your personal information, medical details, and account settings.
              </p>
            </div>

            <div>
              <nav className="relative flex space-x-8 border-b border-gray-200 bg-yellow-100" aria-label="Tabs">
                {tabs.map(tab => (
                  <button
                    key={tab.id}
                    ref={el => (tabRefs.current[tab.id] = el)}
                    onClick={() => setActiveTab(tab.id)}
                    className={`${
                      activeTab === tab.id
                        ? 'border-primary-500 text-primary-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-200 relative bg-transparent`}
                    style={{ background: 'none' }}
                  >
                    {tab.name}
                  </button>
                ))}
                {/* Animated underline */}
                <motion.div
                  ref={underlineRef}
                  layout
                  transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                  className="absolute bottom-0 h-0.5 bg-primary-500 rounded border border-red-500"
                  style={{ left: underlineProps.left, width: underlineProps.width }}
                />
              </nav>
            </div>

            <div className="mt-6 min-h-[200px]">
              <AnimatePresence mode="wait" initial={false}>
                <motion.div
                  key={activeTab}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.4, ease: 'easeInOut' }}
                >
                  {renderTabContent()}
                </motion.div>
              </AnimatePresence>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
