import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import PersonalInfo from './PersonalInfo';
import MedicalInfo from './MedicalInfo';
import AccountSettings from './AccountSettings';

const Profile = () => {
  const [activeTab, setActiveTab] = useState('personal');
  const { user, updateUser } = useAuth();

  const tabs = [
    { id: 'personal', name: 'Personal Information' },
    { id: 'medical', name: 'Medical Information' },
    { id: 'settings', name: 'Account Settings' }
  ];

  const renderTabContent = () => {
    switch (activeTab) {
      case 'personal':
        return <PersonalInfo user={user} onUpdate={updateUser} />;
      case 'medical':
        return <MedicalInfo user={user} />;
      case 'settings':
        return <AccountSettings />;
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow">
          <div className="p-6 space-y-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Profile</h1>
              <p className="mt-2 text-sm text-gray-500">
                Manage your personal information, medical details, and account settings.
              </p>
            </div>

            <div>
              <nav className="flex space-x-8 border-b border-gray-200" aria-label="Tabs">
                {tabs.map(tab => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`${
                      activeTab === tab.id
                        ? 'border-primary-500 text-primary-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-200`}
                  >
                    {tab.name}
                  </button>
                ))}
              </nav>
            </div>

            <div className="mt-6">
              {renderTabContent()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
